import { autoPlayGif } from '../../initial_state';
import unicodeMapping from './emoji_unicode_mapping_light';
import { assetHost } from 'mastodon/utils/config';
import Trie from 'substring-trie';

const trie = new Trie(Object.keys(unicodeMapping));

// Convert to file names from emojis. (For different variation selector emojis)
const emojiFilenames = (emojis) => {
  return emojis.map(v => unicodeMapping[v].filename);
};

// Emoji requiring extra borders depending on theme
const darkEmoji = emojiFilenames(['ğŸ±', 'ğŸœ', 'âš«', 'ğŸ–¤', 'â¬›', 'â—¼ï¸', 'â—¾', 'â—¼ï¸', 'âœ’ï¸', 'â–ªï¸', 'ğŸ’£', 'ğŸ³', 'ğŸ“·', 'ğŸ“¸', 'â™£ï¸', 'ğŸ•¶ï¸', 'âœ´ï¸', 'ğŸ”Œ', 'ğŸ’‚â€â™€ï¸', 'ğŸ“½ï¸', 'ğŸ³', 'ğŸ¦', 'ğŸ’‚', 'ğŸ”ª', 'ğŸ•³ï¸', 'ğŸ•¹ï¸', 'ğŸ•‹', 'ğŸ–Šï¸', 'ğŸ–‹ï¸', 'ğŸ’‚â€â™‚ï¸', 'ğŸ¤', 'ğŸ“', 'ğŸ¥', 'ğŸ¼', 'â™ ï¸', 'ğŸ©', 'ğŸ¦ƒ', 'ğŸ“¼', 'ğŸ“¹', 'ğŸ®', 'ğŸƒ', 'ğŸ´', 'ğŸ', 'ğŸ•º', 'ğŸ“±', 'ğŸ“²', 'ğŸš²']);
const lightEmoji = emojiFilenames(['ğŸ‘½', 'âš¾', 'ğŸ”', 'â˜ï¸', 'ğŸ’¨', 'ğŸ•Šï¸', 'ğŸ‘€', 'ğŸ¥', 'ğŸ‘»', 'ğŸ', 'â•', 'â”', 'â›¸ï¸', 'ğŸŒ©ï¸', 'ğŸ”Š', 'ğŸ”‡', 'ğŸ“ƒ', 'ğŸŒ§ï¸', 'ğŸ', 'ğŸš', 'ğŸ™', 'ğŸ“', 'ğŸ‘', 'ğŸ’€', 'â˜ ï¸', 'ğŸŒ¨ï¸', 'ğŸ”‰', 'ğŸ”ˆ', 'ğŸ’¬', 'ğŸ’­', 'ğŸ', 'ğŸ³ï¸', 'âšª', 'â¬œ', 'â—½', 'â—»ï¸', 'â–«ï¸']);

const emojiFilename = (filename) => {
  const borderedEmoji = (document.body && document.body.classList.contains('theme-mastodon-light')) ? lightEmoji : darkEmoji;
  return borderedEmoji.includes(filename) ? (filename + '_border') : filename;
};

const emojify = (str, customEmojis = {}) => {
  const tagCharsWithoutEmojis = '<&';
  const tagCharsWithEmojis = Object.keys(customEmojis).length ? '<&:' : '<&';
  let rtn = '', tagChars = tagCharsWithEmojis, invisible = 0;
  for (;;) {
    let match, i = 0, tag;
    while (i < str.length && (tag = tagChars.indexOf(str[i])) === -1 && (invisible || !(match = trie.search(str.slice(i))))) {
      i += str.codePointAt(i) < 65536 ? 1 : 2;
    }
    let rend, replacement = '';
    if (i === str.length) {
      break;
    } else if (str[i] === ':') {
      if (!(() => {
        rend = str.indexOf(':', i + 1) + 1;
        if (!rend) return false; // no pair of ':'
        const lt = str.indexOf('<', i + 1);
        if (!(lt === -1 || lt >= rend)) return false; // tag appeared before closing ':'
        const shortname = str.slice(i, rend);
        // now got a replacee as ':shortname:'
        // if you want additional emoji handler, add statements below which set replacement and return true.
        if (shortname in customEmojis) {
          const filename = autoPlayGif ? customEmojis[shortname].url : customEmojis[shortname].static_url;
          replacement = `<img draggable="false" class="emojione custom-emoji" alt="${shortname}" title="${shortname}" src="${filename}" data-original="${customEmojis[shortname].url}" data-static="${customEmojis[shortname].static_url}" />`;
          return true;
        }
        return false;
      })()) rend = ++i;
    } else if (tag >= 0) { // <, &
      rend = str.indexOf('>;'[tag], i + 1) + 1;
      if (!rend) {
        break;
      }
      if (tag === 0) {
        if (invisible) {
          if (str[i + 1] === '/') { // closing tag
            if (!--invisible) {
              tagChars = tagCharsWithEmojis;
            }
          } else if (str[rend - 2] !== '/') { // opening tag
            invisible++;
          }
        } else {
          if (str.startsWith('<span class="invisible">', i)) {
            // avoid emojifying on invisible text
            invisible = 1;
            tagChars = tagCharsWithoutEmojis;
          }
        }
      }
      i = rend;
    } else { // matched to unicode emoji
      const { filename, shortCode } = unicodeMapping[match];
      const title = shortCode ? `:${shortCode}:` : '';
      replacement = `<img draggable="false" class="emojione" alt="${match}" title="${title}" src="${assetHost}/emoji/${emojiFilename(filename)}.svg" />`;
      rend = i + match.length;
      // If the matched character was followed by VS15 (for selecting text presentation), skip it.
      if (str.codePointAt(rend) === 65038) {
        rend += 1;
      }
    }
    rtn += str.slice(0, i) + replacement;
    str = str.slice(rend);
  }
  return rtn + str;
};

export default emojify;

export const buildCustomEmojis = (customEmojis) => {
  const emojis = [];

  customEmojis.forEach(emoji => {
    const shortcode = emoji.get('shortcode');
    const url       = autoPlayGif ? emoji.get('url') : emoji.get('static_url');
    const name      = shortcode.replace(':', '');

    emojis.push({
      id: name,
      name,
      short_names: [name],
      text: '',
      emoticons: [],
      keywords: [name],
      imageUrl: url,
      custom: true,
      customCategory: emoji.get('category'),
    });
  });

  return emojis;
};

export const categoriesFromEmojis = customEmojis => customEmojis.reduce((set, emoji) => set.add(emoji.get('category') ? `custom-${emoji.get('category')}` : 'custom'), new Set(['custom']));
